import{singleClick}from"./modules/hnl.clickhandlers.mjs";export const NAME="simpleToggler";export function init(elements){elements.forEach(function(element){element.querySelectorAll("[data-toggles]").forEach(toggler=>{const toggles=toggler.dataset.toggles;const target=toggler.dataset.target?toggler.dataset.target==="_self"?toggler:document.querySelector(toggler.dataset.target):element;singleClick(toggler,()=>{target.classList.toggle(toggles)})})})};
